// Create Go routes with Gin
package main

import (
    "github.com/gin-gonic/gin"
    "net/http"
)

func healthCheck(c *gin.Context) {
    c.JSON(http.StatusOK, gin.H{"status": "OK"})
}

func setupRoutes(router *gin.Engine) {
    router.GET("/health", healthCheck)
}

